	lcMember = ;
		"UINT              ul:cbSize,"+ ;
		"HWND              ul:hwndOwner,"+ ;
		"HINSTANCE         ul:hInstance,"+ ;
		"LPCTSTR           pz:lpszText,"+ ;
		"LPCTSTR           pz:lpszCaption,"+ ;
