PROCEDURE getcmembers
*====================================================================
* Returns the definition of the structure. We use "z" here for TCHAR
* members.
*====================================================================

	Return ;
		"DWORD      l:dwSize," + ;
		"DWORD      l:dwfOptions," + ;
		; && Location/phone number.
		"DWORD      l:dwCountryID," + ;
		"DWORD      l:dwCountryCode," + ;
		"TCHAR      z11:szAreaCode," + ;
		"TCHAR      z129:szLocalPhoneNumber," + ;
		"DWORD      l:dwAlternateOffset," + ;
		; && PPP/Ip
		"RASIPADDR  o:ipaddr," + ;
		"RASIPADDR  o:ipaddrDns," + ;
		"RASIPADDR  o:ipaddrDnsAlt," + ;
		"RASIPADDR  o:ipaddrWins," + ;
		"RASIPADDR  o:ipaddrWinsAlt," + ;
		; && Framing
		"DWORD      l:dwFrameSize," + ;
		"DWORD      l:dwfNetProtocols," + ;
		"DWORD      l:dwFramingProtocol," + ;
		; && Scripting
		"TCHAR      z260:szScript," + ;
		; && AutoDial
		"TCHAR      z260:szAutodialDLL," + ;
		"TCHAR      z260:szAutodialFunc," + ;
		; && Device
		"TCHAR      z17:szDeviceType," + ;
		"TCHAR      z129:szDeviceName," + ;
		; && X.25
		"TCHAR      z33:szX25PadType," + ;
		"TCHAR      z201:szX25Address," + ;
		"TCHAR      z201:szX25Facilities," + ;
		"TCHAR      z201:szX25UserData," + ;
		"DWORD      l:dwChannels," + ;
		; && Reserved
		"DWORD      l:dwReserved1," + ;
		"DWORD      l:dwReserved2"
ENDPROC
PROCEDURE Init
*====================================================================
* Some initializations are added to make using this structure more
* easy to you.
*====================================================================
With This

	*-----------------------------------------------------------------
	* Initalize structure
	*-----------------------------------------------------------------
	If not DoDefault()
		Return .F.
	Endif

	*-----------------------------------------------------------------
	* Several RASIPADDR structures are used.
	*-----------------------------------------------------------------
	.ipaddr = CreateObject( .cRASIPADDR )
	.ipaddrDns = CreateObject( .cRASIPADDR )
	.ipaddrDnsAlt = CreateObject( .cRASIPADDR )
	.ipaddrWins = CreateObject( .cRASIPADDR )
	.ipaddrWinsAlt = CreateObject( .cRASIPADDR )

	*-----------------------------------------------------------------
	* The dwSize member contains the size of this structure
	*-----------------------------------------------------------------
	.dwSize = .SizeOf()

Endwith
ENDPROC
